CREATE TABLE SGDTEST.TVS_DEPARTAMENTO
(
    IDE_DEPAR NUMBER(18) PRIMARY KEY NOT NULL,
    NOMBRE_DEPAR VARCHAR2(64) DEFAULT NULL
,
    COD_DEPAR VARCHAR2(8) DEFAULT NULL
,
    COD_PAIS VARCHAR2(8) DEFAULT NULL
,
    ESTADO CHAR(2) DEFAULT NULL
,
    FEC_CAMBIO TIMESTAMP(6) DEFAULT SYSDATE
,
    FEC_CREACION TIMESTAMP(6) DEFAULT SYSDATE
,
    COD_USUARIO_CREA VARCHAR2(20) DEFAULT NULL

);
